import React, { useState, useEffect } from 'react';
import { Container } from "react-bootstrap";
import Multiselect from 'multiselect-react-dropdown';
import { Button, Form, Input, Typography } from 'antd'

function SearchBar() {

   
    const [genres, setGenre] = useState([])

    const [suggestions, setSuggestions] = useState([]);
    //const findResult = (genres) => {
    //    setResult(
         // thay gbajg get suggest   suggestions.find((suggestion) => suggestion[suggestionKey] === genres)
    //    );
    //};
    useEffect(() => {
        const getgenredata = async () => {

            const getTenTheLoai = [{ "1": 1}, { "2": 2}, { "3": 3}];
            //const reqData = await fetch("http://localhost/");
            //const resData = await reqData.json();
            

           // for (let i = 0; i < resData.length; i++) {
             //   getTenTheLoai.push(resData[i].nicename);
           // }
            setGenre(getTenTheLoai);
        }
        getgenredata(); // tao cac gia tri de tim
    }, []);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const getSuggestions = [{ "1": 1 }, { "2": 2 }, { "3": 3 }];
            //const { data } = await fetch("http://localhost/");
            //const { ressearch } = await reqData.json();
     

                setSuggestions(getSuggestions);
            } catch (error) {
                console.log(error);
            }
        };

        fetchData();
    }, [genres])

    return (<React.Fragment>
        <div className="Bar">
            <div className="row">
                <div className="col-sm-12">
                    

                    <form className="row g-3" method='post'>

                        <div className="col-md-5">
                            <label className="form-label"> </label>

                            <div className="text-dark">
                                <Multiselect
                                    isObject={false}
                                    onRemove={(event) => { console.log(event) }}
                                    onSelect={(event) => { console.log(event) }}
                                    options={genres}
                                                              
                                    showCheckbox
                                />

                            </div>
                        </div>

                    </form>
                </div>
            </div>
            </div>
        <Button className='button-search' onClick={() => {
            findResult(suggestion.title)
        }}>
            Submit 
        </Button> 
        <div className="tim-thay-phim"> 
            {suggestions.map((suggestion) => (
                <div className="ket-qua-tim">
                    {suggestion}
                </div>
            ))}
        </div>

    </React.Fragment>
    );
}

export default SearchBar
