//Trang Home
import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import "./Home.scss"

function Home() {
    const ScrollAndAddObject = () => {
        const [boxes, setBoxes] = useState([{ id: 1 }]);

        useEffect(() => {
            const handleScroll = () => {
                if (window.innerHeight + window.scrollY >= document.body.offsetHeight) {
                    // Cuộn xuống dưới cùng, thêm đối tượng mới
                    setBoxes(prevBoxes => [...prevBoxes, { id: prevBoxes.length + 1 }]);
                }
            };

            window.addEventListener('scroll', handleScroll);

            // Cleanup listener when component unmounts
            return () => {
                window.removeEventListener('scroll', handleScroll);
            };
        }, []); // useEffect chỉ chạy một lần khi component được mount

        return (
            <div>
                {boxes.map(box => (
                    <div key={box.id} className="box">Vuea them bõx zo nek</div>
                ))}
            </div>
        );
    };


    return (
        <>
            <div className="container" onScroll={ScrollAndAddObject}>
