import { Container } from "react-bootstrap";
import Multiselect from 'multiselect-react-dropdown';
import { Button, Form, Input, Typography } from 'antd'
function SearchBar() {


    const [theLoai, setTheLoai] = useState([]);

    const [suggestions, setSuggestions] = useState([]); //ketqua

    const [hideSuggestions, setHideSuggestions] = useState(true);
    useEffect(() => {
        const getTheLoai = async () => {

            const getTenTheLoai = [{ "1": 1}, { "2": 2}, { "3": 3}];
            //const reqData = await fetch("http://localhost/devopsdeveloper/country");
            //const resData = await reqData.json();
            

           // for (let i = 0; i < resData.length; i++) {
             //   getcountryname.push(resData[i].nicename);
           // }
            setTheLoai(getTenTheLoai);
        }
        getTheLoai();
        
    }, []);
    useEffect(() => {
        const fetchData = async () => {
            
            try {
                const getKetQuaTimKiem = [{ "1": 1 }, { "3": 3 }];


                
                //const reqData = await fetch("http://localhost/devopsdeveloper/country");
                //const resData = await reqData.json();


                // for (let i = 0; i < resData.length; i++) {
                //   getcountryname.push(resData[i].nicename);
                // }

                setSuggestions(getKetQuaTimKiem);
        } catch (error) {
            console.log(error);
        }
    };

       

        fetchData();
    }, [suggestions]);
    

    return (<React.Fragment>

            <div className="row">
                <div className="col-sm-12">
                    

                <form className="row g-3" method='post' onSubmit={(event) => { console.log(event) }}>

                        <div className="col-md-5">
                            <label className="form-label"> </label>

                            <div className="text-dark">
                                <Multiselect
                                isObject={false}
                                onRemove={(event) => { console.log(event) }}
                                onSelect={(event) => { console.log(event) }}
                                options={theLoai}

                                showCheckbox

                                onBlur={() => 

                                    setHideSuggestions(true)
                                }                              
                               / >

                            </div>
                    </div>
                    <input type="submit" onClick={() => setHideSuggestions(false)} />

                    </form>
                </div>
        </div>

        <div
            className="kq">
            {suggestions.map((suggestions) => (
                <div className="jj">
                    KetQUa
                </div>
            ))}
        </div>
         

    </React.Fragment>);
}

export default SearchBar
